return items.map(item => {
  const data = item.json;

  // Função para formatar nome
  function formatarNome(Name) {
    const partes = (Name || "").trim().split(" ");
    const primeiro_nome = partes.shift();
    const sobrenome = partes.length > 0 ? partes.join(" ") : "";
    return { primeiro_nome, sobrenome };
  }

  // Função para formatar WhatsApp
  function formatarWhatsapp(WhatsApp) {
    let numero = (WhatsApp || "").toString().replace(/\D/g, "");
    if (numero.length < 10) return null;
    if (!numero.startsWith("55")) numero = "55" + numero;
    return numero;
  }

  // Processa nome
  const nomeFormatado = formatarNome(data.Nome);

  return {
    json: {
      primeiro_nome: nomeFormatado.primeiro_nome,
      sobrenome: nomeFormatado.sobrenome,
      WhatsApp: formatarWhatsapp(data.WhatsApp),
      Email: (data["E-mail"] || "").toLowerCase(),
      data_hora: null, // campo reservado
      url_pagina: data.pagina || "",
      utm_source: data.utm_source || "",
      utm_medium: data.utm_medium || "",
      utm_content: data.utm_content || "",
      utm_term: data.utm_term || "",
      utm_campaign: data.utm_campaign || ""
    }
  };
});
