return items.map(item => {
  const data = item.json.body.data;

  // 1. Telefone ‚Äî normalizado com DDI
  let telefone = data.buyer?.checkout_phone || "";
  telefone = telefone.replace(/\D/g, "");
  if (!telefone.startsWith("55")) {
    telefone = "55" + telefone;
  }

  // 2. Email
  const email = (data.buyer?.email || "").toLowerCase();

  // 3. Endere√ßo ‚Äî neste caso apenas pa√≠s est√° presente
  const endereco = {
    pais: data.buyer?.address?.country || null
  };
  const enderecoCompleto = endereco.pais || "";

  // 4. UTMs ‚Äî continuam indispon√≠veis
  const utms = {
    utm_source: null,
    utm_medium: null,
    utm_term: null,
    utm_content: null,
    utm_campaign: null
  };

  // 5. Nome e sobrenome
  const nomeCompleto = data.buyer?.name || "";
  const partes = nomeCompleto.trim().split(" ");
  const nome = partes.shift();
  const sobrenome = partes.join(" ");

  // 6. Valores monet√°rios
  const valorTotal = parseFloat(data.purchase?.price?.value || 0);
  const comissao = data.commissions?.reduce((acc, cur) => acc + (cur.value || 0), 0);
  const taxaPlataforma = valorTotal - comissao;

  return {
    json: {
      telefone,
      email,
      enderecoSeparado: endereco,
      enderecoCompleto,
      utms,
      nome,
      sobrenome,

      preco: parseFloat(valorTotal.toFixed(2)),
      comissao: parseFloat(comissao.toFixed(2)),
      taxaPlataforma: parseFloat(taxaPlataforma.toFixed(2)),

      preco_formatado: `R$ ${valorTotal.toFixed(2)}`,
      comissao_formatada: `R$ ${comissao.toFixed(2)}`,
      taxaPlataforma_formatada: `R$ ${taxaPlataforma.toFixed(2)}`
    }
  };
});

