// Função auxiliar para buscar valor em qualquer lugar do objeto por nome da chave
function findValue(obj, keyName) {
  if (!obj || typeof obj !== 'object') return undefined;
  if (obj.hasOwnProperty(keyName)) return obj[keyName];

  for (const key in obj) {
    const value = findValue(obj[key], keyName);
    if (value !== undefined) return value;
  }
  return undefined;
}

// Processamento dinâmico
return items.map(item => {
  const data = item.json;

  // Buscar informações independentemente da posição no JSON
  const phoneRaw = findValue(data, 'phone') || findValue(data, 'checkout_phone') || '';
  const emailRaw = findValue(data, 'email') || '';
  const nameRaw = findValue(data, 'name') || '';

  // Garantir que o telefone começa com "55"
  let phone = phoneRaw.toString();
  if (!phone.startsWith('55') && phone.length > 0) {
    phone = '55' + phone;
  }

  // Email em minúsculo
  const email = emailRaw.toString().toLowerCase();

  // Separar nome e sobrenome
  const [firstName, ...rest] = nameRaw.trim().split(' ');
  const lastName = rest.join(' ');

  return {
    json: {
      ...item.json,
      transformed: {
        phone,
        email,
        firstName,
        lastName
      }
    }
  };
});

