return items.map(item => {
  const data = item.json.body;

  // 1. Telefone ‚Äî normalizado com DDI
  let telefone = data.telefone_ativo || data.telefones || "";
  telefone = telefone.replace(/\D/g, "");
  if (!telefone.startsWith("55")) {
    telefone = "55" + telefone;
  }

  // 2. Email
  const email = (data.email || "").toLowerCase();

  // 3. Endere√ßo
  const endereco = {
    pais: data.endereco_pais || null,
    cep: data.endereco_cep || null,
    estado: data.endereco_estado || null,
    cidade: data.endereco_cidade || null,
    bairro: data.endereco_bairro || null,
    logradouro: data.endereco_logradouro || null,
    numero: data.endereco_numero || null,
    complemento: data.endereco_complemento || null
  };
  const enderecoCompleto = data.endereco_completo || "";

  // 4. UTMs
  const utms = {
    utm_source: data.utm_source || null,
    utm_medium: data.utm_medium || null,
    utm_term: null,
    utm_content: data.utm_content || null,
    utm_campaign: data.utm_campaign || null,
    utm_last_source: data.utm_last_source || null,
    utm_last_medium: data.utm_last_medium || null,
    utm_last_content: data.utm_last_content || null,
    utm_last_campaign: data.utm_last_campaign || null
  };

  // 5. Nome e sobrenome
  const nomeCompleto = data.cliente || "";
  const partes = nomeCompleto.trim().split(" ");
  const nome = partes.shift() || "";
  const sobrenome = partes.join(" ");

  // 6. Documento e nascimento
  const documento = data.documento || null;
  const nascimento = data.nascimento || null;

  // 7. Valores monet√°rios
  const valorTotal = parseFloat(data.valor_total || 0);
  const valorParcela = parseFloat(data.valor_parcela || 0);
  const valorEntrada = parseFloat(data.valor_entrada || 0);
  const taxaAdministracao = parseFloat(data.taxa_administracao || 0);

  // 8. Informa√ß√µes do produto e pagamento
  const produto = {
    nome_produto: data.titulo || null,
    nome_lancamento: data.lancamento || null,
    produtor: data.produtor || null,
    produtor_id: data.produtor_id || null,
    id_transacao: data.id || null,
    id_pedido: data.pedido || null,
    id_externo: data.id_externo || null,
    lancamento_id: data.lancamento_id || null,
    code: data.code || null,
    parcelas: data.parcelas || null,
    melhor_dia_pagamento: data.melhor_dia_pagamento || null,
    status_pedido: data.status_pedido || null,
    status_financeiro: data.status_financeiro || null,
    criado_em: data.criado_em || null,
    data_efetivado: data.data_efetivado || null,
    provedor_negociado: data.provedor_negociado || null
  };

  // 9. Comiss√£o (n√£o presente no input ‚Äî assumido como 0 ou nulo)
  const comissao = parseFloat(data.comissao_produtor || 0); // Adapte aqui se esse campo for adicionado depois
  const taxaPlataforma = valorTotal - comissao;

  return {
    json: {
      // Cliente
      nome,
      sobrenome,
      email,
      telefone,
      documento,
      nascimento,

      // Endere√ßo
      enderecoSeparado: endereco,
      enderecoCompleto,

      // Produto e transa√ß√£o
      produto,

      // UTM
      utms,

      // Valores
      valorTotal: parseFloat(valorTotal.toFixed(2)),
      valorParcela: parseFloat(valorParcela.toFixed(2)),
      valorEntrada: parseFloat(valorEntrada.toFixed(2)),
      taxaAdministracao: parseFloat(taxaAdministracao.toFixed(2)),
      comissao: parseFloat(comissao.toFixed(2)),
      taxaPlataforma: parseFloat(taxaPlataforma.toFixed(2)),

      valorTotal_formatado: `R$ ${valorTotal.toFixed(2)}`,
      valorParcela_formatada: `R$ ${valorParcela.toFixed(2)}`,
      valorEntrada_formatada: `R$ ${valorEntrada.toFixed(2)}`,
      taxaAdministracao_formatada: `R$ ${taxaAdministracao.toFixed(2)}`,
      comissao_formatada: `R$ ${comissao.toFixed(2)}`,
      taxaPlataforma_formatada: `R$ ${taxaPlataforma.toFixed(2)}`
    }
  };
});

