return (() => {
  const data = $json.body;

  // Função para formatar nome
  function formatarNome(Name) {
    const partes = (Name || "").trim().split(" ");
    const primeiro_nome = partes.shift();
    const sobrenome = partes.length > 0 ? partes.join(" ") : "";
    return { primeiro_nome, sobrenome };
  }

  // Função para formatar WhatsApp
  function formatarWhatsapp(WhatsApp) {
    let numero = (WhatsApp || "").replace(/\D/g, "");
    if (numero.length < 10) return null;
    if (!numero.startsWith("55")) numero = "55" + numero;
    return numero;
  }

  // Função para formatar data e hora (caso precise no futuro)
  function formatarDataHora(data, horario) {
    if (!data || !horario) return null;
    return `${data} ${horario}:00`;
  }

  // Dados principais
  const nome = data.Nome;
  const telefone = data.WhatsApp;
  const email = data["E-mail"];
  const pagina = data.pagina || "";

  // UTM Parameters
  const utm_source = data.utm_source || "";
  const utm_medium = data.utm_medium || "";
  const utm_content = data.utm_content || "";
  const utm_term = data.utm_term || "";
  const utm_campaign = data.utm_campaign || "";

  // Processa nome
  const nomeFormatado = formatarNome(nome);

  return {
    primeiro_nome: nomeFormatado.primeiro_nome,
    sobrenome: nomeFormatado.sobrenome,
    WhatsApp: formatarWhatsapp(telefone),
    Email: (email || "").toLowerCase(),
    data_hora: null, // campo não fornecido no input atual
    url_pagina: pagina,
    utm_source,
    utm_medium,
    utm_content,
    utm_term,
    utm_campaign
  };
})();

