const body = $json.body || {};
let telefone = body["WhatsApp"] || "";

// Remove espaços, traços e parênteses
telefone = telefone.replace(/\D/g, "");

// Se não começa com "55", adiciona
if (!telefone.startsWith("55")) {
  telefone = "55" + telefone;
}
body["WhatsApp"] = telefone;

// Verifica e ajusta o campo "No momento você é ?"
if ((body["No momento você é ?"] || "").trim() === "Outro") {
  const outraResposta = body["Outra (escreva aqui)"] || "";
  body["No momento você é ?"] = outraResposta;
}

// Retorna o objeto ajustado
return {
  json: {
    ...$json,
    body
  }
};

