return items.map(item => {
  const data = item.json.body.event;

  // 1. Telefone — normalizado com DDI
  let telefone = data.userPhone || "";
  telefone = telefone.replace(/\D/g, "");

  // Verificar e corrigir números com múltiplos "55" no início
  if (telefone.startsWith("5555")) {
    // Remove os dois primeiros "55" se houver "5555" no início
    telefone = telefone.substring(2);
  } else if (!telefone.startsWith("55")) {
    // Adiciona "55" se não começar com "55"
    telefone = "55" + telefone;
  }

  // 2. Email
  const email = (data.userEmail || "").toLowerCase();

  // 3. Endereço — não está presente, retornando vazio
  const endereco = {};
  const enderecoCompleto = "";

  // 4. UTMs — não disponíveis neste payload, definindo como null
  const utms = {
    utm_source: null,
    utm_medium: null,
    utm_term: null,
    utm_content: null,
    utm_campaign: null
  };

  // 5. Nome e sobrenome
  const nomeCompleto = data.userName || "";
  const partes = nomeCompleto.trim().split(" ");
  const nome = partes.shift();
  const sobrenome = partes.join(" ");

  // 6. Valores monetários
  const valorTotal = parseFloat(data.totalAmount || 0) / 100;

  return {
    json: {
      telefone,
      email,
      enderecoSeparado: endereco,
      enderecoCompleto,
      utms,
      nome,
      sobrenome,
      preco: parseFloat(valorTotal.toFixed(2)),
      comissao: 0,
      taxaPlataforma: 0,
      preco_formatado: `R$ ${valorTotal.toFixed(2)}`,
      comissao_formatada: "R$ 0.00",
      taxaPlataforma_formatada: "R$ 0.00"
    }
  };
});
