return (() => {
  const data = $json.body;

  // Lista oficial de DDDs válidos no Brasil
  const dddsValidos = [
    "11","12","13","14","15","16","17","18","19",
    "21","22","24","27","28",
    "31","32","33","34","35","37","38",
    "41","42","43","44","45","46",
    "47","48","49",
    "51","53","54","55",
    "61","62","63","64","65","66","67",
    "68","69",
    "71","73","74","75","77","79",
    "81","82","83","84","85","86","87","88","89",
    "91","92","93","94","95","96","97","98","99"
  ];

  // Separa nome completo em primeiro nome e sobrenome
  function formatarNome(Name) {
    const partes = (Name || "").trim().split(" ");
    const primeiro_nome = partes.shift();
    const sobrenome = partes.length > 0 ? partes.join(" ") : "";
    return { primeiro_nome, sobrenome };
  }

  // Formata número de WhatsApp (inclui fallback para DDD "98" se ausente)
  function formatarWhatsapp(WhatsApp) {
    let numero = (WhatsApp || "").replace(/\D/g, "");

    // Adiciona DDI do Brasil se não tiver
    if (!numero.startsWith("55")) {
      numero = "55" + numero;
    }

    let numeroNacional = numero.slice(2); // Remove "55"

    // Se vier sem DDD (8 ou 9 dígitos), assume "98"
    if (numeroNacional.length === 8 || numeroNacional.length === 9) {
      numeroNacional = "98" + numeroNacional;
    }

    const ddd = numeroNacional.slice(0, 2);
    let restante = numeroNacional.slice(2);

    // Verifica DDD válido
    if (!dddsValidos.includes(ddd)) {
      console.log("❌ DDD inválido:", ddd);
      return null;
    }

    // Corrige erro de digitação (9999 → 999)
    if (/^9999/.test(restante)) {
      restante = restante.replace(/^9999/, '999');
      numeroNacional = ddd + restante;
    }

    // Validação final (deve ter 10 ou 11 dígitos)
    if (numeroNacional.length !== 10 && numeroNacional.length !== 11) {
      console.log("❌ Número inválido:", numeroNacional);
      return null;
    }

    const resultado = '55' + numeroNacional;

    console.log({ input: WhatsApp, resultado });

    return resultado;
  }

  // Dados principais do formulário
  const nome = data.Nome;
  const telefone = data.WhatsApp;
  const email = data["E-mail"];
  const pagina = data.pagina || "";

  // Parâmetros UTM
  const utm_source = data.utm_source || "";
  const utm_medium = data.utm_medium || "";
  const utm_content = data.utm_content || "";
  const utm_term = data.utm_term || "";
  const utm_campaign = data.utm_campaign || "";

  const nomeFormatado = formatarNome(nome);

  // Retorna no formato esperado pelo Code node
  return [
    {
      json: {
        primeiro_nome: nomeFormatado.primeiro_nome,
        sobrenome: nomeFormatado.sobrenome,
        WhatsApp: formatarWhatsapp(telefone),
        Email: (email || "").toLowerCase(),
        data_hora: null,
        url_pagina: pagina,
        utm_source,
        utm_medium,
        utm_content,
        utm_term,
        utm_campaign
      }
    }
  ];
})();

